function gallerySlider() {
    var swiper = new Swiper('.gallery-slider .swiper-container', {
        slidesPerView: "auto",
        spaceBetween: 0,
        navigation: {
            nextEl: '.gallery-slider .swiper-button-next',
            prevEl: '.gallery-slider .swiper-button-prev',
        },
    })
}

function indexDoctorsSlider() {
    var swiper = new Swiper('.doctors-slider .swiper-container', {
        slidesPerView: 4,
        spaceBetween: 0,
        observer: true,
        observeParents: true,
        observeSlideChildren: true,
        grid: {
            fill: 'row',
            rows: 1,
        },
        navigation: {
            nextEl: '.doctors-slider .swiper-button-next',
            prevEl: '.doctors-slider .swiper-button-prev',
        },
        breakpoints: {
            320: {
                slidesPerView: 2,
                grid: {
                    fill: 'row',
                    rows: 2,
                },
            },
            576: {
                slidesPerView: 3,
                grid: {
                    fill: 'row',
                    rows: 1,
                },
            },
            992: {
                slidesPerView: 4,
                grid: {
                    fill: 'row',
                    rows: 1,
                },
            },
        },
    })
}

indexDoctorsSlider()

gallerySlider()


$(".letters__item").click(function () {
    let id = $(this).attr("data-id")

    if($(this).hasClass("_active")){
        $(".letters__item").removeClass("_active")
        $(".letters").removeClass("_selected")
        $(".alphabet-items").addClass("_show-all")
        $(".letter-group").removeClass("_active")
    } else{
        $(".letters__item").removeClass("_active")
        $(this).addClass("_active")
        $(".letters").addClass("_selected")
        $(".alphabet-items").removeClass("_show-all")
        $(".letter-group").removeClass("_active")
        $(`.letter-group[data-id=${id}]`).addClass("_active")
    }
})





$(document).ready(function() {
    $(".mob-tabs2 select").change(function () {
        let id = $(".mob-tabs2 option:selected").attr("value")
        setTimeout(()=>{
            $(`.heal-types .tab[data-tab-path="${id}"]`).click()
        },10)
    })
    $(".heal-types .tab").mouseup(function () {
        let id = $(this).attr("data-tab-path")
        $(`.mob-tabs2 option`).prop('selected', false)
        $(`.mob-tabs2 option[value="${id}"]`).prop('selected', true)
        $(".mob-tabs2 select").niceSelect('update')
    })
})

